function myprefix_search_filter( $query ) {
	if ( ! is_admin() && $query->is_main_query() ) {
		if ( $query->is_search ) {
			$query->set('post_type', 'post');
		}
	}
}
add_action( 'pre_get_posts','myprefix_search_filter' );

// Add related items setting for standard posts
add_filter( 'wpex_metabox_array', function( $array, $post ) {

	if ( 'post' == $post->post_type ) {

	    $array['main']['settings']['related_post_ids'] = array(
	        'title'         => __( 'Related Posts', 'total' ),
	        'description'   => __( 'Comma seperated ID\'s for related items', 'total' ),
	        'id'            => 'related_post_ids', 
	        'type'          => 'text',
	    );

	}

    // Return fields
    return $array;

}, 40, 2 );

// Alter related items if the related_post_ids meta field is set
add_filter( 'wpex_blog_post_related_query_args', function ( $args ) {
	
	if ( $related = get_post_meta( get_the_ID(), 'related_post_ids', true ) ) {
		$args[ 'category__in' ] = null;
		$args[ 'post__in' ] = explode( ',', $related );
	}

	// Return args
	return $args; 

} );

/**
* Example showing how to change author bio URL
*
* @link http://wpexplorer-themes.com/total/snippets/alter-author-bio-url/
* @see  Total/author-bio.php
*
*/
function myprefix_author_bio_url( $data ) {

	// Get global post
	global $post;

	// Add custom URL for post author with ID of 1
	if ( 1 == $post->post_author ) {
		$url = '/danielbieli';
	}

	// Use posts url for all other authors
	else {
		$url = get_author_posts_url( $post->post_author );
	}

	// Alter the post url for the author bio
	$data['posts_url'] = $url;

	// Return data
	return $data;

}
add_filter( 'wpex_post_author_bio_data', 'myprefix_author_bio_url' );

