/**
* Example showing how to change author bio URL
*
* @link http://wpexplorer-themes.com/total/snippets/alter-author-bio-url/
* @see  Total/author-bio.php
*
*/
function myprefix_author_bio_url( $data ) {

	// Get global post
	global $post;

	// Add custom URL for post author with ID of 1
	if ( 1 == $post->post_author ) {
		$url = '/danielbieli';
	}

	// Use posts url for all other authors
	else {
		$url = get_author_posts_url( $post->post_author );
	}

	// Alter the post url for the author bio
	$data['posts_url'] = $url;

	// Return data
	return $data;

}
