function woocommerce_enable_hide_out_of_stock_items( $template_name, $template_path, $located, $args ) {
    if( $template_name !== "single-product/related.php" ) {
        return;
    }
 
    add_filter( 'pre_option_woocommerce_hide_out_of_stock_items', function( $option ) { return "yes"; }, 10, 1 );
}
 
/**
 * Temporarily disable hide out of stock items.
 *
 * @param string $template_name
 * @param string $template_path
 * @param bool $located
 * @param array $args
 */
function woocommerce_disable_hide_out_of_stock_items( $template_name, $template_path, $located, $args ) {
    if( $template_name !== "single-product/related.php" ) {
        return;
    }
 
    add_filter( 'pre_option_woocommerce_hide_out_of_stock_items', function( $option ) { return "no"; }, 10, 1 );
}
 
add_action( 'woocommerce_before_template_part', 'woocommerce_enable_hide_out_of_stock_items', 10, 4 );
add_action( 'woocommerce_after_template_part', 'woocommerce_disable_hide_out_of_stock_items', 10, 4 );
